package com.example.smallwhite.config;import lombok.extern.slf4j.Slf4j;import org.springframework.web.servlet.support.AbstractAnnotationConfigDispatcherServletInitializer;/** * @author: yangqiang * @create: 2020-04-15 10:14 */@Slf4jpublic class DispatcherServlet extends AbstractAnnotationConfigDispatcherServletInitializer {    @Override    protected Class<?>[] getRootConfigClasses() {        return new Class[0];    }    @Override    protected Class<?>[] getServletConfigClasses() {        return new Class[0];    }    @Override    protected String[] getServletMappings() {        log.info("DispatcherServlet请求");        return new String[]{"/"};    }}