package com.example.smallwhite.config.rabbitmq;import lombok.extern.slf4j.Slf4j;import org.springframework.amqp.rabbit.connection.CorrelationData;import org.springframework.amqp.rabbit.core.RabbitTemplate;import java.io.IOException;/** * 判断消息是否正确到达交换机中 * 只要到了交换机中 返回的ack标志就是true 其他地方出现异常 由ReturnCallBack来进行处理 * @author: yangqiang * @create: 2021-03-06 13:45 */@Slf4jpublic class ConfirmCallBackConfig implements RabbitTemplate.ConfirmCallback {    /**     * @param correlationData 唯一消息标识     * @param ack 确认消息是否成功发送到交换机中     * @param cause 失败原因     * */    @Override    public void confirm(CorrelationData correlationData, boolean ack, String cause) {        log.info("ConfirmCallback:     " + "相关数据：" + correlationData);        log.info("ConfirmCallback:     " + "确认情况：" + ack);        log.info("ConfirmCallback:     " + "原因：" + cause);    }}