package com.example.smallwhite.config.rabbitmq;import lombok.extern.slf4j.Slf4j;import org.springframework.amqp.core.Message;import org.springframework.amqp.rabbit.core.RabbitTemplate;/** * 消息投递失败的返回 * 比如不给交换机绑定任何队列 获取根据路由键找不到队列 * @author: yangqiang * @create: 2021-03-06 13:48 */@Slf4jpublic class ReturnCallBackConfig implements RabbitTemplate.ReturnCallback {    /**     * @param  message 交换机发送失败的消息     * @param  replyCode 错误码     * @param  replyText 错误信息     * @param  exchange 是哪个交换机返回的投递失败信息     * @param  routingKey 路由键     * */    @Override    public void returnedMessage(Message message, int replyCode, String replyText, String exchange, String routingKey) {        log.info("ReturnCallback:     " + "消息：" + message);        log.info("ReturnCallback:     " + "回应码：" + replyCode);        log.info("ReturnCallback:     " + "回应信息：" + replyText);        log.info("ReturnCallback:     " + "交换机：" + exchange);        log.info("ReturnCallback:     " + "路由键：" + routingKey);    }}