package com.example.smallwhite.config.websocket;import lombok.AllArgsConstructor;import lombok.Data;import java.io.Serializable;import java.sql.Timestamp;/** * @author: yangqiang * @create: 2020-06-23 15:29 * websocket消息实体类 */@Data@AllArgsConstructorpublic class MessagePoJo implements Serializable {    private static final long serialVersionUID = -6451812593150428369L;    /**     * 来自谁的消息     */    private String from;    /**     * 发送给谁     */    private String to;    /**     * 消息内容     */    private Object data;    /**     * 消息时间     */    private Timestamp time = new Timestamp(System.currentTimeMillis());    public MessagePoJo(String from, String to, Object data) {        this.from = from;        this.to = to;        this.data = data;    }}