package com.example.smallwhite.config.websocket;import org.springframework.beans.BeansException;import org.springframework.beans.factory.BeanFactory;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import javax.websocket.server.ServerEndpointConfig;/** * @author: yangqiang * @create: 2020-06-16 19:17 */public class MyEndpointConfigure extends ServerEndpointConfig.Configurator implements ApplicationContextAware {    private static volatile BeanFactory context;    @Override    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        MyEndpointConfigure.context = applicationContext;    }}