package com.example.smallwhite.designpatterns.bridge;import lombok.extern.slf4j.Slf4j;import java.math.BigDecimal;/** * @author: yangqiang * @create: 2021-02-24 10:30 */@Slf4jpublic abstract class Pay {    /**     * 支付模式 刷脸指纹密码     *     * */    protected IPayMode payMode;    public Pay(IPayMode payMode) {        this.payMode = payMode;    }    public abstract String transfer(String uId, String tradeId, BigDecimal amount);}