package com.example.smallwhite.designpatterns.bridge;import java.math.BigDecimal;/** * 分离抽象和具体 * 用聚合（桥接）方式连接抽象和具体 * 比如支付方式有 支付宝支付 微信支付 具体的支付方式 * 支付模式有 人脸 指纹 密码 三种支付模式 一级更多抽象的支付模式 * @author: yangqiang * @create: 2021-02-24 10:38 */public class Test {    public static void main(String[] args) {        System.out.println("\r\n模拟测试场景；微信支付、人脸方式。");        Pay wxPay = new WxPay(new PayFaceMode());        wxPay.transfer("weixin_1092033111", "100000109893", new BigDecimal(100));        System.out.println("\r\n模拟测试场景；支付宝支付、指纹方式。");        Pay zfbPay = new ZfbPay(new PayFingerprintMode());        zfbPay.transfer("jlu19dlxo111","100000109894",new BigDecimal(100));    }}