package com.example.smallwhite.designpatterns.chainofresponsibility.V1;import java.util.*;import java.util.ArrayList;/** * 责任链模式 整合所有业务filter * @author: yangqiang * @create: 2021-02-27 14:36 */public class FilterChain implements Filter{    List<Filter> filters= new ArrayList<Filter>();    public FilterChain add(Filter filter){        filters.add(filter);        return this;    }    public FilterChain remove(Filter filter){        filters.remove(filter);        return this;    }    @Override    public boolean doFilter(Message message){        for (Filter filter : filters){            if(!filter.doFilter(message)) {                return false;            }        }        return true;    }}