package com.example.smallwhite.designpatterns.chainofresponsibility.V1;/** * @author: yangqiang * @create: 2021-02-27 14:31 */public class HtmlFilter implements  Filter{    @Override    public boolean doFilter(Message message) {        String r = message.getMsg();        r = r.replace("<","[");        r = r.replace(">","]");        message.setMsg(r);        return true;    }}