package com.example.smallwhite.designpatterns.chainofresponsibility.V2;import com.example.smallwhite.designpatterns.chainofresponsibility.V1.Message;import lombok.Data;import java.util.ArrayList;import java.util.List;/** * 责任链模式 整合所有业务filter * @author: yangqiang * @create: 2021-02-27 14:36 */@Datapublic class FilterChain implements Filter {    List<Filter> filters= new ArrayList<Filter>();    public FilterChain add(Filter filter){        filters.add(filter);        return this;    }    public FilterChain remove(Filter filter){        filters.remove(filter);        return this;    }    @Override    public boolean doFilter(Request req, Response resp, FilterChain filterChain) {        return false;    }}