package com.example.smallwhite.designpatterns.command;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import java.util.*;/** * @author: yangqiang * @create: 2021-02-27 16:45 */public class XiaoEr {    private Logger logger = LoggerFactory.getLogger(XiaoEr.class);    private List<ICuisine> cuisineList = new ArrayList<ICuisine>();    public void order(ICuisine cuisine) {        cuisineList.add(cuisine);    }    public synchronized void placeOrder() {        for (ICuisine cuisine : cuisineList) {            cuisine.cook();        }        cuisineList.clear();    }}