package com.example.smallwhite.designpatterns.composite;/** * @author: yangqiang * @create: 2021-02-24 19:48 */public abstract class File {    String name;    public File(String name){        this.name = name;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public abstract void display();}