package com.example.smallwhite.designpatterns.decorator;/** * @author: yangqiang * @create: 2021-02-23 23:06 */public class CookieSsoDecorator extends SsoInterceptor{    public CookieSsoDecorator(HandlerInterceptor handlerInterceptor) {        super(handlerInterceptor);    }    @Override    public boolean preHandle(String request, String response, Object handler) {        System.out.println("校验成功");        return handlerInterceptor.preHandle(request, response, handler);    }}