package com.example.smallwhite.designpatterns.mediator.V1;/** * @author: yangqiang * @create: 2021-03-01 20:38 */public class Farmer {    private int score;    public Farmer() {        this.score = 0;    }    public void win(Farmer anotherFarmer, Landlord landlord) {        this.score += 10;        anotherFarmer.win(anotherFarmer,landlord);        landlord.lose();    }    public void lose() {        this.score -= 10;    }}