package com.example.smallwhite.designpatterns.mediator.V1;/** * @author: yangqiang * @create: 2021-03-01 20:38 */// 地主类public class Landlord {    private int score; // 现有分数    public Landlord() {        this.score = 0;    }    public void win(Farmer[] farmers) {        this.score += 20;        for(Farmer farmer : farmers) {            farmer.lose();        }    }    public void lose() {        this.score -= 20;    }}