package com.example.smallwhite.designpatterns.observer.V2;import com.example.smallwhite.designpatterns.observer.V1.LotteryResult;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * MQ发送事件 * @author: yangqiang * @create: 2021-03-02 18:54 */public class MQEventListener implements EventListener {    private Logger logger = LoggerFactory.getLogger(MQEventListener.class);    @Override    public void doEvent(LotteryResult result) {        logger.info("记录用户 {} 摇号结果(MQ)：{}", result.getUId(), result.getMsg());    }}