package com.example.smallwhite.designpatterns.observer.V2;import com.example.smallwhite.designpatterns.observer.V1.LotteryResult;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * 短消息事件 * @author: yangqiang * @create: 2021-03-02 18:53 */public class MessageEventListener implements EventListener {    private Logger logger = LoggerFactory.getLogger(MessageEventListener.class);    @Override    public void doEvent(LotteryResult result) {        logger.info("给用户 {} 发送短信通知(短信)：{}", result.getUId(), result.getMsg());    }}