package com.example.smallwhite.designpatterns.proxy.V1;/** * @author: yangqiang * @create: 2021-02-25 22:05 */public class TimeProxy implements MoveAble{    Tank tank;    public TimeProxy() {        this.tank = new Tank();    }    @Override    public void move() {        long start = System.currentTimeMillis();        tank.move();        long end = System.currentTimeMillis();        System.out.println("tank跑了"+(end-start)+" ms");    }}