package com.example.smallwhite.designpatterns.proxy.V2;/** * @author: yangqiang * @create: 2021-02-25 22:10 */public class LogProxy implements MoveAble{    MoveAble moveAble;    public LogProxy(MoveAble moveAble) {        this.moveAble = moveAble;    }    @Override    public void move() {        System.out.println("tank开始移动");        moveAble.move();        System.out.println("tank移动结束");    }}