package com.example.smallwhite.designpatterns.proxy.V2;import java.security.SecureRandom;/** * @author: yangqiang * @create: 2021-02-25 22:02 */public class Tank implements MoveAble {    @Override    public void move() {        System.out.println("移动中");        try {            Thread.sleep(new SecureRandom().nextInt(10000));        } catch (InterruptedException e) {            e.printStackTrace();        }    }}