package com.example.smallwhite.designpatterns.state;/** * @author: yangqiang * @create: 2021-03-02 19:56 */public class MembershipController implements LevelController {    private MembershipState membershipState;    private void setMembershipState(MembershipState membershipState) {        this.membershipState = membershipState;    }    @Override    public void setLevel(MembershipState state) {        setMembershipState(state);    }    public void refund() {        membershipState.refund();    }    public void purchase() {        membershipState.purchase();    }}