package com.example.smallwhite.designpatterns.state;/** * 状态模式 * 通过修改状态，而去控制动作。 * @author: yangqiang * @create: 2021-03-02 19:56 */public class Test {    public static void main(String[] args) {        MembershipController membershipController = new MembershipController();        membershipController.setLevel(new L0State());        membershipController.refund();        membershipController.setLevel(new L1State());        membershipController.refund();        membershipController.setLevel(new L0State());        membershipController.purchase();    }}