package com.example.smallwhite.designpatterns.strategy;import java.math.BigDecimal;/** * @author: yangqiang * @create: 2021-03-02 20:17 */public class NYGCouponDiscount implements ICouponDiscount<Double> {    /**     * n元购购买     * 1. 无论原价多少钱都固定金额购买     */    @Override    public BigDecimal discountAmount(Double couponInfo, BigDecimal skuPrice) {        return new BigDecimal(couponInfo);    }}