package com.example.smallwhite.designpatterns.template;import sun.misc.BASE64Encoder;import sun.net.www.http.HttpClient;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * @author: yangqiang * @create: 2021-03-02 20:27 */public class JDNetMall extends NetMall {    public JDNetMall(String uId, String uPwd) {        super(uId, uPwd);    }    @Override    public Boolean login(String uId, String uPwd) {        logger.info("模拟京东用户登录 uId：{} uPwd：{}", uId, uPwd);        return true;    }    @Override    public Map<String, String> reptile(String skuUrl) {        Pattern p9 = Pattern.compile("(?<=title\\>).*(?=</title)");        Matcher m9 = p9.matcher(skuUrl);        Map<String, String> map = new ConcurrentHashMap<String, String>();        if (m9.find()) {            map.put("name", m9.group());        }        map.put("price", "5999.00");        logger.info("模拟京东商品爬虫解析：{} | {} 元 {}", map.get("name"), map.get("price"), skuUrl);        return map;    }    @Override    public String createBase64(Map<String, String> goodsInfo) {        BASE64Encoder encoder = new BASE64Encoder();        logger.info("模拟生成京东商品base64海报");        return encoder.encode(goodsInfo.toString().getBytes());    }}