package com.example.smallwhite.designpatterns.visitor.visitor.impl;import com.example.smallwhite.designpatterns.visitor.user.impl.Student;import com.example.smallwhite.designpatterns.visitor.user.impl.Teacher;import com.example.smallwhite.designpatterns.visitor.visitor.Visitor;import org.slf4j.Logger;import org.slf4j.LoggerFactory;/** * @author: yangqiang * @create: 2021-03-02 20:49 */public class Principal implements Visitor {    private Logger logger = LoggerFactory.getLogger(Principal.class);    @Override    public void visit(Student student) {        logger.info("学生信息 姓名：{} 班级：{}", student.name, student.clazz);    }    @Override    public void visit(Teacher teacher) {        logger.info("学生信息 姓名：{} 班级：{} 升学率：{}", teacher.name, teacher.clazz, teacher.entranceRatio());    }}