package com.example.smallwhite.entity;import lombok.Data;import java.sql.Timestamp;/** * 备忘录 * @author: yangqiang * @create: 2020-06-22 09:37 */@Datapublic class Event {    /**     * 事件id     */    public String id;    /**     *事件状态 0--未完成 1--已完成     */    public Integer state;    /**     * 事件内容     * */    public String content;    /**     * 修改时间     * */    public Timestamp ts;    /**     * 删除状态     * */    public Integer dr;    public String getTableName() {        return "EVENT";    }}