package com.example.smallwhite.java8;import lombok.AllArgsConstructor;import lombok.Data;import lombok.NoArgsConstructor;import lombok.ToString;import java.util.function.Supplier;/** * @author: yangqiang * @create: 2021-03-05 10:46 */@Data@AllArgsConstructor@NoArgsConstructor@ToStringpublic class MethodRefer {    String name;    String id;    public static MethodRefer create( final Supplier< MethodRefer > supplier ) {        return supplier.get();    }    public static void collide( final MethodRefer car ) {        System.out.println( "Collided " + car.toString() );    }    public  void follow(final MethodRefer another) {        System.out.println( "Following the " + another.toString() );    }    public void repair() {        System.out.println( "Repaired " + this.toString() );    }}