package com.example.smallwhite.juc;import java.util.concurrent.ConcurrentHashMap;/** * @author: yangqiang * @create: 2021-04-27 09:59 */public class ConcurrentHashMapTest {    public static void main(String[] args) {        ConcurrentHashMap<String, Object> map = new ConcurrentHashMap<String,Object>();        for (int i = 0; i < 47; i++) {            map.put("abc"+i,i);        }    }}