package com.example.smallwhite.jvm.jit;import java.util.concurrent.LinkedBlockingQueue;import java.util.concurrent.ThreadPoolExecutor;import java.util.concurrent.TimeUnit;/** * @author: yangqiang * @create: 2021-06-02 09:55 */public class JustInTimeTest2 {    static boolean stopRequested = false;    public static void main(String[] args) throws InterruptedException {        ThreadPoolExecutor executor = new ThreadPoolExecutor(1, 1, 1, TimeUnit.SECONDS, new LinkedBlockingQueue<>());        executor.execute(()->{        int i = 0;        while (!stopRequested) {            System.out.println(i++);        }        });        executor.shutdown();        TimeUnit.MICROSECONDS.sleep(10);        stopRequested = true;    }}