package com.example.smallwhite.lang;import com.google.common.util.concurrent.ThreadFactoryBuilder;import java.util.concurrent.LinkedBlockingQueue;import java.util.concurrent.ThreadFactory;import java.util.concurrent.ThreadPoolExecutor;import java.util.concurrent.TimeUnit;/** * ThrealLocal * @author: yangqiang * @create: 2021-05-31 10:10 */public class ThreadLocalTest {    public static void main(String[] args) {        ThreadPoolExecutor threadPool = new ThreadPoolExecutor(Integer.MAX_VALUE, Integer.MAX_VALUE, 1, TimeUnit.MILLISECONDS, new LinkedBlockingQueue<Runnable>());        ThreadLocal<Object> local = new ThreadLocal<>();        ThreadLocal<Object> local2 = new ThreadLocal<>();        for (int i = 0; i < 100; i++) {            int finalI = i;            threadPool.execute(()->{                local.set(Thread.currentThread().getName());                local2.set("yangqiang"+ finalI);                System.out.println(local.get());            });        }    }}