package com.example.smallwhite.leetcode.june;import java.util.HashMap;/** * @author: yangqiang * @create: 2021-06-01 16:45 */public class TwoSum {    public static void main(String[] args) {    }    public int[] twoSum(int[] nums, int target) {        HashMap<Integer, Integer> map = new HashMap<>();        for (int i = 0; i < nums.length; i++) {            if(map.containsKey(target-nums[i])){                nums[0] = map.get(target-nums[i]);                nums[1] = map.get(nums[i]);                return nums;            }            map.put(nums[i],i);        }        return nums;    }}