package com.example.smallwhite.leetcode.may;/** * 10100 * 01010 * 汉明距离 * @author: yangqiang * @create: 2021-05-27 19:56 */public class hanmingDistance {    public static void main(String[] args) {        System.out.println(distance(10,20));    }    public static int distance(int x,int y){        int count = 0;        int num = x ^ y;        while (num > 0 ){            if((num & 1) == 1){                count ++;            }            num >>=1;        }        return count;    }}