package com.example.smallwhite.mybatis.controller;import com.example.smallwhite.mybatis.entity.Baby;import com.example.smallwhite.mybatis.service.BabyService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;/** * (Babyimage)表控制层 * * @author makejava * @since 2020-10-09 14:17:13 */@RestController@RequestMapping("baby")public class BabyController {    /**     * 服务对象     */    @Autowired    private BabyService babyService;    /**     * 通过主键查询单条数据     *     * @param id 主键     * @return 单条数据     */    @GetMapping("selectOne")    public Baby selectOne(String id) {        return this.babyService.queryById(id);    }}