package com.example.smallwhite.mybatis.controller;import com.example.smallwhite.mybatis.entity.Babyimage;import com.example.smallwhite.mybatis.service.BabyimageService;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;/** * (Babyimage)表控制层 * * @author makejava * @since 2020-10-09 14:17:13 */@RestController@RequestMapping("babyimage")public class BabyimageController {    /**     * 服务对象     */    @Resource    private BabyimageService babyimageService;    /**     * 通过主键查询单条数据     *     * @param id 主键     * @return 单条数据     */    @GetMapping("selectOne")    public Babyimage selectOne(String id) {        return this.babyimageService.queryById(id);    }}