package com.example.smallwhite.mybatis.dao;import com.example.smallwhite.mybatis.entity.Baby;import org.apache.ibatis.annotations.Mapper;import org.apache.ibatis.annotations.Param;import java.util.List;/** * (baby)表数据库访问层 * * @author makejava * @since 2020-10-09 14:17:07 */@Mapperpublic interface BabyDao {    /**     * 通过ID查询单条数据     *     * @param id 主键     * @return 实例对象     */    Baby queryById(@Param("id") String id);    /**     * 查询指定行数据     *     * @param offset 查询起始位置     * @param limit  查询条数     * @return 对象列表     */    List<Baby> queryAllByLimit(@Param("offset") int offset, @Param("limit") int limit);    /**     * 通过实体作为筛选条件查询     *     * @param baby 实例对象     * @return 对象列表     */    List<Baby> queryAll(Baby baby);    /**     * 新增数据     *     * @param baby 实例对象     * @return 影响行数     */    int insert(Baby baby);    /**     * 批量新增数据（MyBatis原生foreach方法）     *     * @param entities List<baby> 实例对象列表     * @return 影响行数     */    int insertBatch(@Param("entities") List<Baby> entities);    /**     * 批量新增或按主键更新数据（MyBatis原生foreach方法）     *     * @param entities List<baby> 实例对象列表     * @return 影响行数     */    int insertOrUpdateBatch(@Param("entities") List<Baby> entities);    /**     * 修改数据     *     * @param baby 实例对象     * @return 影响行数     */    int update(Baby baby);    /**     * 通过主键删除数据     *     * @param id 主键     * @return 影响行数     */    int deleteById(String id);    Object insertBatchRetIds(List<Baby> list);}