package com.example.smallwhite.mybatis.service.impl;import com.example.smallwhite.mybatis.dao.BabyDao;import com.example.smallwhite.mybatis.entity.Baby;import com.example.smallwhite.mybatis.service.BabyService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * (Babyimage)表服务实现类 * * @author makejava * @since 2020-10-09 14:17:12 */@Servicepublic class BabyServiceImpl implements BabyService {    @Autowired    private BabyDao babyDao;    /**     * 通过ID查询单条数据     *     * @param id 主键     * @return 实例对象     */    @Override    public Baby queryById(String id) {        return this.babyDao.queryById(id);    }    /**     * 查询多条数据     *     * @param offset 查询起始位置     * @param limit  查询条数     * @return 对象列表     */    @Override    public List<Baby> queryAllByLimit(int offset, int limit) {        return this.babyDao.queryAllByLimit(offset, limit);    }    /**     * 新增数据     *     * @param baby 实例对象     * @return 实例对象     */    @Override    public Baby insert(Baby baby) {        this.babyDao.insert(baby);        return baby;    }    /**     * 修改数据     *     * @param baby 实例对象     * @return 实例对象     */    @Override    public Baby update(Baby baby) {        this.babyDao.update(baby);        return this.queryById(baby.getId());    }    /**     * 通过主键删除数据     *     * @param id 主键     * @return 是否成功     */    @Override    public boolean deleteById(String id) {        return this.babyDao.deleteById(id) > 0;    }}