package com.example.smallwhite.mybatis.service.impl;import com.example.smallwhite.mybatis.dao.BabyimageDao;import com.example.smallwhite.mybatis.entity.Babyimage;import com.example.smallwhite.mybatis.service.BabyimageService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * (Babyimage)表服务实现类 * * @author makejava * @since 2020-10-09 14:17:12 */@Servicepublic class BabyimageServiceImpl implements BabyimageService {    @Autowired    private BabyimageDao babyimageDao;    /**     * 通过ID查询单条数据     *     * @param id 主键     * @return 实例对象     */    @Override    public Babyimage queryById(String id) {        return this.babyimageDao.queryById(id);    }    /**     * 查询多条数据     *     * @param offset 查询起始位置     * @param limit  查询条数     * @return 对象列表     */    @Override    public List<Babyimage> queryAllByLimit(int offset, int limit) {        return this.babyimageDao.queryAllByLimit(offset, limit);    }    /**     * 新增数据     *     * @param babyimage 实例对象     * @return 实例对象     */    @Override    public Babyimage insert(Babyimage babyimage) {        this.babyimageDao.insert(babyimage);        return babyimage;    }    /**     * 修改数据     *     * @param babyimage 实例对象     * @return 实例对象     */    @Override    public Babyimage update(Babyimage babyimage) {        this.babyimageDao.update(babyimage);        return this.queryById(babyimage.getId());    }    /**     * 通过主键删除数据     *     * @param id 主键     * @return 是否成功     */    @Override    public boolean deleteById(String id) {        return this.babyimageDao.deleteById(id) > 0;    }}