package com.example.smallwhite.reflex.V1;/** * 反射破坏单例模式 * @author: yangqiang * @create: 2021-03-29 18:58 */public class Singleton {    private static Singleton instance = new Singleton();    /**     * 调用构造方法的次数     */    public static int times;    public Singleton() {        System.out.println("调用了"+(++times)+"次构造方法");        if(instance!=null){            throw new IllegalArgumentException("单例构造器不能重复使用");        }    }    public static Singleton getInstance(){        return instance;    }}