package com.example.smallwhite.test;import com.example.smallwhite.entity.Baby;import com.example.smallwhite.entity.testClass;import java.util.HashMap;/** * i++ 操作是先进行比较符操作 再进行++操作 所以是用原来的值和 * * @author: yangqiang * @create: 2021-03-15 10:34 */public class IAddAddTest {    public static void main(String[] args) {        int i = 0;        i = i++;        System.out.println("i = " + i);        int a = 2;        int b = (3 * a++) + a;        System.out.println(b);        a = 2;        b = a + (3 * a++);        System.out.println(b);        a = 0;        b = 0;        a = a++;        b = a++;        System.out.println("a = " + a + ", b = " + b);    }}