package com.example.smallwhite.thread;/** * @author: yangqiang * @create: 2021-03-08 20:23 */public class Main {    public volatile static int n = 0;    public static final int LIMIT = 100;    public static final int THREAD_COUNT = 3;    public static class ARunnable implements Runnable {        int mode;        public ARunnable(int mode){            this.mode = mode;        }        @Override        public void run(){            while(n <= LIMIT){                if (n % THREAD_COUNT == mode && n <= LIMIT) {                    System.out.println(n);                    n++;                } else {                    Thread.yield();                }            }        }    }    public static void main(String[] arg){        new Thread(new ARunnable(0)).start();        new Thread(new ARunnable(1)).start();        new Thread(new ARunnable(2)).start();    }}