package com.example.smallwhite.thread;/** * @author: yangqiang * @create: 2021-03-08 19:53 */public class MyThread1 implements Runnable {    @Override    public  void run() {        while (StaticNum.num < 100) {            Integer flag =1;            synchronized (StaticNum.class) {                if (StaticNum.num % 2 == 1) {                    System.out.println("线程" + Thread.currentThread().getName() + "打印的" + StaticNum.num++);                } else {                    Thread.yield();                    Thread.currentThread().notify();                    flag++;                    if(flag ==1){                        System.out.println(Thread.currentThread().getName()+"线程，状态："+Thread.currentThread().getState().toString());                    }                }            }        }    }}