package com.example.smallwhite.thread;/** * @author: yangqiang * @create: 2021-03-08 19:56 */public class MyThread2 implements Runnable {    @Override    public synchronized void run() {        while (StaticNum.num < 100) {            Integer flag = 0;            synchronized (StaticNum.class) {                if (StaticNum.num % 2 == 0) {                    System.out.println("线程" + Thread.currentThread().getName() + "打印的" + StaticNum.num++);                } else {                    Thread.yield();                    flag++;                    if(flag ==1){                        System.out.println(Thread.currentThread().getName()+"线程，状态："+Thread.currentThread().getState().toString());                    }                }            }        }    }}