package com.example.smallwhite.thread;import java.util.concurrent.locks.ReentrantLock;/** * @author: yangqiang * @create: 2021-03-13 15:36 */public class ReentrantLockTest {    public static void main(String[] args) {        ReentrantLock reentrantLock = new ReentrantLock();        for (int i = 0; i < 10; i++) {            new Thread(()->{                reentrantLock.lock();                try {                    System.out.println(Thread.currentThread().getName()+"拿到了锁");                }catch (Exception e){                }finally {                    reentrantLock.unlock();                }            },"线程"+i).start();        }    }}