package com.example.smallwhite.thread;import java.time.LocalDateTime;import java.util.concurrent.Executors;import java.util.concurrent.ScheduledExecutorService;import java.util.concurrent.TimeUnit;/** * ScheduledThreadPool执行延时任务 *   多线程执行多延时任务 *   单线程执行多延时任务 * @author: yangqiang * @create: 2021-03-24 22:33 */public class TestScheduledThreadPool {    public static void main(String[] args) {        ScheduledExecutorService threadPool = Executors.newScheduledThreadPool(5);        Thread thread = new Thread(new Runnable() {            @Override            public void run() {                System.out.println(Thread.currentThread().getName() + "进来了," +                        "当前时间是 " + LocalDateTime.now());            }        });        threadPool.scheduleAtFixedRate(thread, 5, 2, TimeUnit.SECONDS);    }}