package com.example.smallwhite.thread;import java.util.concurrent.ThreadFactory;/** * @author: yangqiang * @create: 2021-03-08 14:03 */public class TestThreadFactory implements ThreadFactory {    String threadName = "";    public TestThreadFactory(String threadName) {        this.threadName = threadName;    }    @Override    public Thread newThread(Runnable r) {        return new Thread(r,this.threadName);    }}