package com.example.smallwhite.thread;import com.example.smallwhite.thread.mythread.CallableImp;import com.example.smallwhite.thread.mythread.RunnableImp;import com.example.smallwhite.thread.mythread.ThreadExtend;import java.util.concurrent.FutureTask;/** * @author: yangqiang * @create: 2021-03-08 19:57 */public class ThreadTest {    public static void main(String[] args) throws Exception {        ThreadExtend threadExtend = new ThreadExtend("my-thread");        threadExtend.start();        Thread thread = new Thread(new RunnableImp(), "my-runnable");        thread.start();        CallableImp callableImp = new CallableImp();        FutureTask futureTask = new FutureTask(callableImp);        Thread callableThread = new Thread(futureTask, "my-callable");        callableThread.start();        System.out.println(futureTask.get());    }}