package com.example.smallwhite.utils;import org.springframework.web.WebApplicationInitializer;import javax.servlet.*;/** * @author: yangqiang * @create: 2020-04-20 16:16 */public class MyServletInit implements WebApplicationInitializer {    @Override    public void onStartup(ServletContext servletContext) throws ServletException {        servletContext.addServlet("myServlet", MyServlet.class).addMapping("/test");        servletContext.addFilter("myFilter", (Class<? extends Filter>) MyFilter.class).addMappingForUrlPatterns(null,false,"/custom/**");    }}