package com.example.smallwhite.utils.excel.impl;import com.example.smallwhite.utils.JdbcUtil;import com.example.smallwhite.utils.excel.service.DownloadService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * 对导入的excel文件进行批量新增操作 * @author: yangqiang * @create: 2020-09-14 14:37 */@Servicepublic class DownloadServiceImpl implements DownloadService {    @Autowired    JdbcUtil jdbcUtil;    @Override    public boolean excelInsert(List<?> data) {        return jdbcUtil.insert(data)==data.size();    }}