package com.example.smallwhite.utils.excel.impl;import com.alibaba.excel.EasyExcel;import com.example.smallwhite.utils.excel.ExcelListener;import com.example.smallwhite.utils.excel.service.DownloadService;import com.example.smallwhite.utils.excel.service.ExportService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.web.multipart.MultipartFile;import java.io.IOException;/** * @author: yangqiang * @create: 2020-09-14 14:28 */@Servicepublic class ExportServiceInsertImpl implements ExportService {    @Autowired    DownloadService service;    @Override    public void insert(MultipartFile file,Class clazz) throws IOException {        EasyExcel.read(file.getInputStream(),clazz,new ExcelListener(service)).sheet().doRead();    }}