package com.example.smallwhite.utils.mybatis;import org.apache.ibatis.mapping.BoundSql;import org.apache.ibatis.mapping.SqlSource;/** * @author: yangqiang * @create: 2021-01-19 16:21 */public class BoundSqlSqlSource implements SqlSource {    public BoundSql boundSql;    public BoundSqlSqlSource(BoundSql boundSql) {        this.boundSql = boundSql;    }    @Override    public BoundSql getBoundSql(Object parameterObject) {        return boundSql;    }}